import React, { Component } from 'react'
import whitePawn from '../images/Chess_plt60.png'

class Square extends Component{
  constructor(props){
    super(props)
    this.state = {
      whitePawn: false,
      whiteKnight: false,
      whiteBishop: false,
      whiteRook: false,
      whiteQueen: false,
      whiteKing: false,
      blackPawn: false,
      blackKnight: false,
      blackBishop: false,
      blackRook: false,
      blackQueen: false,
      blackKing: false,
    }
  }

  showPiece = () => {
    switch (this.props.boardState[this.props.index])  {
      case 'whitePawn':
        if (this.state.whitePawn !== true) {
          this.setState({
            whitePawn: true,
            whiteKnight: false,
            whiteBishop: false,
            whiteRook: false,
            whiteQueen: false,
            whiteKing: false
          })
        }
      break;
      case 'whiteKnight':
        if (this.state.whiteKnight !== true) {
          this.setState({
            whitePawn: false,
            whiteKnight: true,
            whiteBishop: false,
            whiteRook: false,
            whiteQueen: false,
            whiteking: false
          })
        }
      break;
      case 'whiteBishop':
        if (this.state.whiteBishop !== true) {
          this.setState({
            whitePawn: false,
            whiteKnight: false,
            whiteBishop: true,
            whiteRook: false,
            whiteQueen: false,
            whiteKing: false
          })
        }
      break;
      case 'whiteRook':
        if (this.state.whiteRook !== true) {
          this.setState({
            whitePawn: false,
            whiteKnight: false,
            whiteBishop: false,
            whiteRook: true,
            whiteQueen: false,
            whiteKing: false
          })
        }
      break;
      case 'whiteQueen':
        if (this.state.whiteQueen !== true) {
          this.setState({
            whitePawn: false,
            whiteKnight: false,
            whiteBishop: false,
            whiteRook: false,
            whiteQueen: true,
            whiteKing: false
          })
        }
      break;
      case 'whiteKing':
        if (this.state.whiteKing !== true) {
          this.setState({
            whitePawn: false,
            whiteKnight: false,
            whiteBishop: false,
            whiteRook: false,
            whiteQueen: false,
            whiteKing: true
          })
        }
      break
      case 'blackPawn':
        if (this.state.blackPawn !== true) {
          this.setState({
            blackPawn: true,
            blackKnight: false,
            blackBishop: false,
            blackRook: false,
            blackQueen: false,
            blackKing: false
          })
        }
      break;
      case 'blackKnight':
        if (this.state.blackKnight !== true) {
          this.setState({
            blackPawn: false,
            blackKnight: true,
            blackBishop: false,
            blackRook: false,
            blackQueen: false,
            blackking: false
          })
        }
      break;
      case 'blackBishop':
        if (this.state.blackBishop !== true) {
          this.setState({
            blackPawn: false,
            blackKnight: false,
            blackBishop: true,
            blackRook: false,
            blackQueen: false,
            blackKing: false
          })
        }
      break;
      case 'blackRook':
        if (this.state.blackRook !== true) {
          this.setState({
            blackPawn: false,
            blackKnight: false,
            blackBishop: false,
            blackRook: true,
            blackQueen: false,
            blackKing: false
          })
        }
      break;
      case 'blackQueen':
        if (this.state.blackQueen !== true) {
          this.setState({
            blackPawn: false,
            blackKnight: false,
            blackBishop: false,
            blackRook: false,
            blackQueen: true,
            blackKing: false
          })
        }
      break;
      case 'blackKing':
        if (this.state.blackKing !== true) {
          this.setState({
            blackPawn: false,
            blackKnight: false,
            blackBishop: false,
            blackRook: false,
            blackQueen: false,
            blackKing: true
          })
        }
      break;
      default:
    }
  }

  render(){
    return(
      <React.Fragment>
        { this.showPiece() }
        <div
          id = "square"
          style = { { backgroundColor: this.props.colorGrid[this.props.index] } }
        >
        <div> { this.state.whitePawn && <img src = { whitePawn } /> } </div>
        <div> { this.state.whiteKnight && "whiteKnight"} </div>
        <div> { this.state.whiteBishop && "whiteBishop"} </div>
        <div> { this.state.whiteRook && "whiteRook"} </div>
        <div> { this.state.whiteQueen && "whiteQueen"} </div>
        <div> { this.state.whiteKing && "whiteKing"} </div>
        <div> { this.state.blackPawn && "blackPawn"} </div>
        <div> { this.state.blackKnight && "blackKnight"} </div>
        <div> { this.state.blackBishop && "blackBishop"} </div>
        <div> { this.state.blackRook && "blackRook"} </div>
        <div> { this.state.blackQueen && "blackQueen"} </div>
        <div> { this.state.blackKing && "blackKing"} </div>
        </div>
      </React.Fragment>
    )
  }
}
export default Square
