import React, { Component } from 'react'
import Square from './components/Square'
import './App.css'

class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      grid:[...Array(64).fill("white")],
    }
  }











  render(){
    let grid = this.state.grid.map((value,index)=> {
      return (
        <Square
          index = {index}
          value = {value}
        />
      )
    })
    return(
      <React.Fragment>

        <h1>CHESS</h1>
        <div> { grid } </div>

      </React.Fragment>
    )
  }
}
export default App
