import React, { Component } from 'react'

class Square extends Component{
  constructor(props){
    super(props)
    this.state = {
      whitePawn: false,
      whiteKnight: false,
      whiteBishop: false,
      whiteRook: false,
      whiteQueen: false,
      whiteKing: false,
      blackPawn: false,
      blackKnight: false,
      blackBishop: false,
      blackRook: false,
      blackQueen: false,
      blackKing: false,
    }
  }

  showPiece = () => {
    switch (this.props.boardState[this.props.index])  {
      case 'whitePawn':
        if (this.state.whitePawn !== true) {
          this.setState({
            whitePawn: true,
            whiteKnight: false,
            whiteBishop: false,
            whiteRook: false,
            whiteQueen: false,
            whiteKing: false
          })
        }
      break;
      case 'whiteKnight':
        if (this.state.whiteKnight !== true) {
          this.setState({
            whitePawn: false,
            whiteKnight: true,
            whiteBishop: false,
            whiteRook: false,
            whiteQueen: false,
            whiteking: false
          })
        }
      break;
      case 'whiteBishop':
        if (this.state.whiteBishop !== true) {
          this.setState({
            whitePawn: false,
            whiteKnight: false,
            whiteBishop: true,
            whiteRook: false,
            whiteQueen: false,
            whiteKing: false
          })
        }
      break;
      case 'whiteRook':
        if (this.state.whiteRook !== true) {
          this.setState({
            whitePawn: false,
            whiteKnight: false,
            whiteBishop: false,
            whiteRook: true,
            whiteQueen: false,
            whiteKing: false
          })
        }
      break;
      case 'whiteQueen':
        if (this.state.whiteQueen !== true) {
          this.setState({
            whitePawn: false,
            whiteKnight: false,
            whiteBishop: false,
            whiteRook: false,
            whiteQueen: true,
            whiteKing: false
          })
        }
      break;
      case 'whiteKing':
        if (this.state.whiteKing !== true) {
          this.setState({
            whitePawn: false,
            whiteKnight: false,
            whiteBishop: false,
            whiteRook: false,
            whiteQueen: false,
            whiteKing: true
          })
        }
      break
    }
  }

  render(){
    return(
      <React.Fragment>
        { this.showPiece() }
        <div
          id = "square"
          style = { { backgroundColor: this.props.colorGrid[this.props.index] } }
        >
        <div> { this.state.whitePawn && "whitePawn"} </div>
        <div> { this.state.whiteKnight && "whiteKnight"} </div>
        <div> { this.state.whiteBishop && "whiteBishop"} </div>
        <div> { this.state.whiteRook && "whiteRook"} </div>
        <div> { this.state.whiteQueen && "whiteQueen"} </div>
        <div> { this.state.whiteKing && "whiteKing"} </div>
        </div>
      </React.Fragment>
    )
  }
}
export default Square
