          currentArray[random +2 ] = 4

          //subtracts from the amount of ships remaining
          threeShipHor--
      }
    }
    while (threeShipVert > 0) {
      let random = Math.floor(Math.random() * 100)
      console.log("threeShipVert start:",random)
