import React, { Component } from 'react'

class Square extends Component{
  constructor(props){
    super(props)
    this.state = {
      pawn: false,
      knight: false,
      bishop: false,
      rook: false,
      queen: false,
      king: false
    }
  }

  showPiece = () => {
    switch (this.props.boardState[this.props.index])  {
      case 'pawn':
        if (this.state.pawn !== true) {
          this.setState({
            pawn: true,
            knight: false,
            bishop: false,
            rook: false,
            queen: false,
            king: false
          })
        }
      break;
      case 'knight':
        if (this.state.knight !== true) {
          this.setState({
            pawn: false,
            knight: true,
            bishop: false,
            rook: false,
            queen: false,
            king: false
          })
        }
      break;
      case 'bishop':
        if (this.state.bishop !== true) {
          this.setState({
            pawn: false,
            knight: false,
            bishop: true,
            rook: false,
            queen: false,
            king: false
          })
        }
      break;
      case 'rook':
        if (this.state.rook !== true) {
          this.setState({
            pawn: false,
            knight: false,
            bishop: false,
            rook: true,
            queen: false,
            king: false
          })
        }
      break;
      case 'queen':
        if (this.state.queen !== true) {
          this.setState({
            pawn: false,
            knight: true,
            bishop: false,
            rook: false,
            queen: true,
            king: false
          })
        }
      break;
      case 'king':
        if (this.state.king !== true) {
          this.setState({
            pawn: false,
            knight: false,
            bishop: false,
            rook: false,
            queen: false,
            king: true
          })
        }
      break;
    }
  }

  render(){
    return(
      <React.Fragment>
        { this.showPiece() }
        <div
          id = "square"
          style = { { backgroundColor: this.props.colorGrid[this.props.index] } }
        >
        <div> { this.state.pawn && "pawn"} </div>
        <div> { this.state.knight && "knight"} </div>
        <div> { this.state.knight && "bishop"} </div>
        <div> { this.state.knight && "rook"} </div>
        <div> { this.state.knight && "queen"} </div>
        <div> { this.state.knight && "king"} </div>
        </div>
      </React.Fragment>
    )
  }
}
export default Square
