import React, { Component } from 'react'

class Square extends Component{
  constructor(props){
    super(props)
    this.state = {
      pawn: false,
      knight: false,
      bishop: false,
      rook: false,
      queen: false,
      king: false
    }
  }

  showPiece = () => {
    switch (this.props.boardState[this.props.index])  {
      case 'pawn':
        if (this.state.pawn !== true) {
          this.setState({
            pawn: true,
            knight: false,
            bishop: false,
            rook: false,
            queen: false,
            king: false
          })
        }
      case 'knight':
        if (this.state.knight !== true) {
          this.setState({
            pawn: false,
            knight: true,
            bishop: false,
            rook: false,
            queen: false,
            king: false
          })
        }
    }
  }

  render(){
    return(
      <React.Fragment>
        { this.showPiece() }
        <div
          id = "square"
          style = { { backgroundColor: this.props.colorGrid[this.props.index] } }
        >
        <div> { this.state.pawn && "pawn"} </div>
        </div>
      </React.Fragment>
    )
  }
}
export default Square
