import React, { Component } from 'react'
import Square from './components/Square'
import './App.css'

class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      colorGrid:[...Array(64).fill("green")],
    }
  }











  render(){
    let board = this.state.colorGrid.map((value,index)=> {
      return (
        <Square
          index = { index }
          value = { value }
          colorGrid = { this.state.colorGrid }
        />
      )
    })
    return(
      <React.Fragment>

        <h1>CHESS</h1>
        <div id = "boardHolder">
          <div id = "boardHolderTwo">
            <div> { board } </div>
          </div>
        </div>

      </React.Fragment>
    )
  }
}
export default App
